    func getEntryForPoint(point: PointDictionaries.Point, key: PointDictionaries.Key) -> String {
        
        var string = ""
        let thisDictionary = getPointDictionary(point)
        var keyString = ""
        
        let thisKey = key

        switch thisKey {
        case .Classifications : keyString = self.classifications
        case .Location : keyString = self.location
        case .Meetings : keyString = self.meetings
        case .Needling : keyString = self.needling
        case .Warning : keyString = self.warning
        case .Actions : keyString = self.actions
        case .Neuro : keyString = self.neuro
        case .Trigger : keyString = self.trigger
        }
        
        if thisDictionary.keys.contains(keyString){
            string = thisDictionary[keyString]!
        } else {
            print("\(point) dictionary doesn't contain key \(keyString)")
        }
        return string
    }
    
    
